import React from 'react';
import { motion } from 'framer-motion';
import { 
  UserGroupIcon,
  ClockIcon,
  SparklesIcon
} from '@heroicons/react/24/outline';

const RestaurantBarSection = () => {
  const features = [
    { icon: UserGroupIcon, text: 'Restaurant cu 40 de locuri' },
    { icon: SparklesIcon, text: 'Bar modern cu cocktailuri speciale și băuturi de calitate' },
    { icon: ClockIcon, text: 'Potrivit pentru mic dejun, prânz, cină și evenimente private' },
  ];

  return (
    <section className="section-padding bg-background-primary">
      <div className="container-custom grid grid-cols-1 md:grid-cols-2 gap-12 items-center">
        <motion.div
          initial={{ opacity: 0, x: -30 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <h2 className="text-3xl md:text-4xl font-display font-bold text-text-primary mb-4">
            Restaurant & Bar
          </h2>
          <p className="text-lg text-text-light mb-6">
            Bucătărie rafinată românească și mediteraneană într-un cadru elegant și intim.
          </p>
          <ul className="space-y-3 mb-6">
            {features.map((feature, idx) => {
              const IconComponent = feature.icon;
              return (
                <li key={idx} className="flex items-center space-x-3">
                  <span className="inline-flex items-center justify-center w-8 h-8 bg-primary-100 rounded-full">
                    <IconComponent className="w-5 h-5 text-primary-500" />
                  </span>
                  <span className="text-text-secondary">{feature.text}</span>
                </li>
              );
            })}
          </ul>
        </motion.div>
        <motion.div
          initial={{ opacity: 0, x: 30 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
          className="grid grid-cols-2 gap-4"
        >
          <img
            src="https://images.unsplash.com/photo-1504674900247-0877df9cc836?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80"
            alt="Salonul restaurantului"
            className="rounded-xl shadow-lg object-cover h-36 w-full"
            loading="lazy"
          />
          <img
            src="https://images.unsplash.com/photo-1519864600265-abb23847ef2c?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80"
            alt="Barul hotelului"
            className="rounded-xl shadow-lg object-cover h-36 w-full"
            loading="lazy"
          />
        </motion.div>
      </div>
    </section>
  );
};

export default RestaurantBarSection;
