import React from 'react';
import { motion } from 'framer-motion';
import { 
  HeartIcon,
  SparklesIcon,
  CheckIcon
} from '@heroicons/react/24/outline';

const FitnessRoomSection = () => {
  const features = [
    { icon: HeartIcon, text: 'Spațiu dedicat mișcării' },
    { icon: SparklesIcon, text: 'Aparate de bază pentru întreținere fizică' },
    { icon: CheckIcon, text: 'Inclus în tariful camerei' },
  ];

  return (
    <section className="section-padding bg-background-secondary">
      <div className="container-custom grid grid-cols-1 md:grid-cols-2 gap-12 items-center">
        <motion.div
          initial={{ opacity: 0, x: -30 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <h2 className="text-3xl md:text-4xl font-display font-bold text-text-primary mb-4">
            Sala Fitness
          </h2>
          <p className="text-lg text-text-light mb-6">
            Menține-ți forma și energia în timpul șederii la Hotel Marami.
          </p>
          <ul className="space-y-3 mb-6">
            {features.map((feature, idx) => {
              const IconComponent = feature.icon;
              return (
                <li key={idx} className="flex items-center space-x-3">
                  <span className="inline-flex items-center justify-center w-8 h-8 bg-primary-100 rounded-full">
                    <IconComponent className="w-5 h-5 text-primary-500" />
                  </span>
                  <span className="text-text-secondary">{feature.text}</span>
                </li>
              );
            })}
          </ul>
        </motion.div>
        <motion.div
          initial={{ opacity: 0, x: 30 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
          className="relative"
        >
          <img
            src="https://images.unsplash.com/photo-1571019613454-1cb2f99b2d8b?ixlib=rb-4.0.3&auto=format&fit=crop&w=2070&q=80"
            alt="Sala fitness Hotel Marami"
            className="rounded-xl shadow-lg w-full h-80 object-cover"
            loading="lazy"
          />
        </motion.div>
      </div>
    </section>
  );
};

export default FitnessRoomSection;
