import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { StarIcon, ChatBubbleLeftIcon } from '@heroicons/react/24/solid';
import { ChevronLeftIcon, ChevronRightIcon } from '@heroicons/react/24/outline';

const TestimonialsSection = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const testimonials = [
    {
      id: 1,
      name: 'Maria Popescu',
      location: 'București, România',
      rating: 5,
      text: 'O experiență excepțională! Camerele sunt impecabile, personalul este foarte prietenos și locația este perfectă pentru a explora Sinaia. Recomand cu încredere!',
      avatar: 'https://images.unsplash.com/photo-1494790108755-2616b612b786?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80'
    },
    {
      id: 2,
      name: 'Alexandru Ionescu',
      location: 'Cluj-Napoca, România',
      rating: 5,
      text: 'Hotel Marami a depășit toate așteptările mele. Spa-ul este fantastic, restaurantul servește mâncare delicioasă, iar vedere la munți este spectaculoasă. Voi reveni cu siguranță!',
      avatar: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80'
    },
    {
      id: 3,
      name: 'Elena Dumitrescu',
      location: 'Timișoara, România',
      rating: 5,
      text: 'Perfect pentru o escapadă romantică! Suite-ul presidențial este luxos, serviciile sunt de top, iar atenția la detalii este remarcabilă. Mulțumim pentru o ședere memorabilă!',
      avatar: 'https://images.unsplash.com/photo-1438761681033-6461ffad8d80?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80'
    },
    {
      id: 4,
      name: 'David Müller',
      location: 'Berlin, Germania',
      rating: 5,
      text: 'Am călătorit în multe locuri, dar Hotel Marami este cu adevărat special. Combinația perfectă între tradiție românească și confort modern. Personalul vorbesc germana și sunt foarte atenți!',
      avatar: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80'
    },
    {
      id: 5,
      name: 'Ana Vasilescu',
      location: 'Iași, România',
      rating: 5,
      text: 'Pentru a treia oară la Hotel Marami și de fiecare dată experiența este și mai bună. Facilitațile sunt excelente, iar atmosfera este relaxantă. Recomand tuturor!',
      avatar: 'https://images.unsplash.com/photo-1544005313-94ddf0286df2?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80'
    }
  ];

  const nextTestimonial = () => {
    setCurrentIndex((prev) => (prev + 1) % testimonials.length);
  };

  const prevTestimonial = () => {
    setCurrentIndex((prev) => (prev - 1 + testimonials.length) % testimonials.length);
  };

  const goToTestimonial = (index) => {
    setCurrentIndex(index);
  };

  return (
    <section className="section-padding bg-gradient-to-br from-primary-50 to-warm-50">
      <div className="container-custom">
        {/* Section Header */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl md:text-4xl lg:text-5xl font-display font-bold text-text-primary mb-4">
            Ce Spun Oaspeții Noștri
          </h2>
          <p className="text-lg text-text-light max-w-2xl mx-auto">
            Descoperă experiențele autentice ale oaspeților noștri și de ce aleg 
            Hotel Marami pentru șederile lor în Sinaia.
          </p>
        </motion.div>

        {/* Overall Rating */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
          className="text-center mb-12"
        >
          <div className="flex justify-center items-center space-x-2 mb-4">
            {[...Array(5)].map((_, i) => (
              <StarIcon key={i} className="w-8 h-8 text-yellow-400 fill-current" />
            ))}
          </div>
          <p className="text-2xl font-display font-bold text-text-primary mb-2">
            4.9/5
          </p>
          <p className="text-text-light">
            Bazat pe {testimonials.length * 50}+ recenzii
          </p>
        </motion.div>

        {/* Testimonials Carousel */}
        <div className="relative max-w-4xl mx-auto">
          <AnimatePresence mode="wait">
            <motion.div
              key={currentIndex}
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -50 }}
              transition={{ duration: 0.5 }}
              className="bg-white p-8 md:p-12 shadow-xl"
            >
              <div className="text-center">
                {/* Quote Icon */}
                <div className="w-16 h-16 bg-primary-100 flex items-center justify-center mx-auto mb-6">
                  <ChatBubbleLeftIcon className="w-8 h-8 text-primary-600" />
                </div>

                {/* Rating */}
                <div className="flex justify-center space-x-1 mb-6">
                  {[...Array(testimonials[currentIndex].rating)].map((_, i) => (
                    <StarIcon key={i} className="w-5 h-5 text-yellow-400 fill-current" />
                  ))}
                </div>

                {/* Testimonial Text */}
                <blockquote className="text-lg md:text-xl text-text-secondary leading-relaxed mb-8 italic">
                  "{testimonials[currentIndex].text}"
                </blockquote>

                {/* Author */}
                <div className="flex items-center justify-center space-x-4">
                  <img
                    src={testimonials[currentIndex].avatar}
                    alt={testimonials[currentIndex].name}
                    className="w-16 h-16 rounded-full object-cover"
                    loading="lazy"
                  />
                  <div className="text-left">
                    <p className="font-display font-semibold text-text-primary">
                      {testimonials[currentIndex].name}
                    </p>
                    <p className="text-text-light text-sm">
                      {testimonials[currentIndex].location}
                    </p>
                  </div>
                </div>
              </div>
            </motion.div>
          </AnimatePresence>

          {/* Navigation Buttons */}
          <button
            onClick={prevTestimonial}
            className="absolute left-4 top-1/2 transform -translate-y-1/2 w-12 h-12 bg-white shadow-lg flex items-center justify-center hover:bg-gray-50 transition-colors"
            aria-label="Testimonial anterior"
          >
            <ChevronLeftIcon className="w-6 h-6 text-text-primary" />
          </button>

          <button
            onClick={nextTestimonial}
            className="absolute right-4 top-1/2 transform -translate-y-1/2 w-12 h-12 bg-white shadow-lg flex items-center justify-center hover:bg-gray-50 transition-colors"
            aria-label="Testimonial următor"
          >
            <ChevronRightIcon className="w-6 h-6 text-text-primary" />
          </button>
        </div>

        {/* Dots Indicator */}
        <motion.div
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6, delay: 0.3 }}
          className="flex justify-center space-x-2 mt-8"
        >
          {testimonials.map((_, index) => (
            <button
              key={index}
              onClick={() => goToTestimonial(index)}
              className={`w-3 h-3 transition-all duration-300 ${
                index === currentIndex
                  ? 'bg-primary-600 w-8'
                  : 'bg-gray-300 hover:bg-gray-400'
              }`}
              aria-label={`Mergi la testimonial ${index + 1}`}
            />
          ))}
        </motion.div>

        {/* CTA */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8, delay: 0.4 }}
          className="text-center mt-12"
        >
          <p className="text-text-light mb-4">
            Vrei să împărtășești și tu experiența ta? Lasă-ne o recenzie!
          </p>
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="btn-primary"
          >
            Lasă o Recenzie
          </motion.button>
        </motion.div>
      </div>
    </section>
  );
};

export default TestimonialsSection;
