import React from 'react';
import { motion } from 'framer-motion';
import { 
  TruckIcon,
  TrainIcon,
  PaperAirplaneIcon
} from '@heroicons/react/24/outline';

const HowToReachSection = () => {
  const transportOptions = [
    {
      icon: TruckIcon,
      title: 'Cu mașina',
      description: '5 min de la centrul orașului Sinaia',
      details: [
        'Din București: DN1 → Sinaia (aproximativ 1h 30min)',
        'Din Brașov: DN1 → Sinaia (aproximativ 45min)',
        'Parcare gratuită disponibilă la hotel'
      ],
      color: 'bg-blue-500'
    },
    {
      icon: TrainIcon,
      title: 'Cu trenul',
      description: 'Gara Sinaia → 10–15 min pe jos / 5 min cu taxi',
      details: [
        'Trenuri regulate din București Nord (aproximativ 2h)',
        'Trenuri regulate din Brașov (aproximativ 1h)',
        'Transfer cu taxi disponibil la cerere'
      ],
      color: 'bg-green-500'
    },
    {
      icon: PaperAirplaneIcon,
      title: 'Cu avionul',
      description: 'Aeroport Otopeni → ≈1h 30 min cu mașina (transfer disponibil)',
      details: [
        'Aeroportul Internațional Henri Coandă (OTP)',
        'Transfer privat disponibil la cerere',
        'Distanță: aproximativ 120 km'
      ],
      color: 'bg-purple-500'
    }
  ];

  return (
    <section className="section-padding bg-background-primary">
      <div className="container-custom">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl md:text-4xl font-display font-bold text-text-primary mb-4">
            Cum Ajungi la Noi
          </h2>
          <p className="text-lg text-text-light max-w-2xl mx-auto">
            Găsește-ne ușor, indiferent de modul de transport ales.
          </p>
        </motion.div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {transportOptions.map((option, index) => {
            const IconComponent = option.icon;
            return (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.8, delay: index * 0.2 }}
                whileHover={{ y: -5 }}
                className="bg-white rounded-2xl shadow-lg p-8 hover:shadow-xl transition-all duration-300"
              >
                <div className={`inline-flex items-center justify-center w-12 h-12 ${option.color} text-white rounded-xl mb-6`}>
                  <IconComponent className="w-6 h-6" />
                </div>

                <h3 className="text-xl font-bold text-text-primary mb-3">
                  {option.title}
                </h3>

                <p className="text-text-secondary mb-4 font-medium">
                  {option.description}
                </p>

                <ul className="space-y-2">
                  {option.details.map((detail, detailIndex) => (
                    <li key={detailIndex} className="text-sm text-text-light flex items-start">
                      <span className="text-primary-500 mr-2">•</span>
                      {detail}
                    </li>
                  ))}
                </ul>
              </motion.div>
            );
          })}
        </div>

        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8, delay: 0.6 }}
          className="mt-12 bg-primary-50 rounded-2xl p-8 text-center"
        >
          <h3 className="text-2xl font-bold text-text-primary mb-4">
            Ai nevoie de ajutor cu transportul?
          </h3>
          <p className="text-text-secondary mb-6">
            Oferim servicii de transfer și putem să te ajutăm să organizezi transportul.
          </p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <a
              href="tel:+40244315000"
              className="bg-primary-500 hover:bg-primary-600 text-white px-6 py-3 rounded-lg font-semibold transition-colors"
            >
              Sună pentru transfer
            </a>
            <a
              href="mailto:contact@hotelmarami.ro"
              className="bg-white hover:bg-gray-50 text-primary-500 border border-primary-500 px-6 py-3 rounded-lg font-semibold transition-colors"
            >
              Trimite email
            </a>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default HowToReachSection;
