import React from 'react';
import { motion } from 'framer-motion';
import { 
  UserGroupIcon,
  TvIcon,
  WifiIcon,
  PhoneIcon,
  ComputerDesktopIcon,
  SparklesIcon
} from '@heroicons/react/24/outline';

const ConferenceRoomSection = () => {
  const equipment = [
    { icon: PhoneIcon, text: 'Telefon, fax, internet gratuit' },
    { icon: TvIcon, text: 'TV, video, DVD' },
    { icon: ComputerDesktopIcon, text: 'Retroproiector, videoproiector, flip chart' },
    { icon: SparklesIcon, text: 'Climatizare' },
    { icon: ComputerDesktopIcon, text: 'Birou auxiliar cu PC și telefon' },
  ];

  return (
    <section className="section-padding bg-background-primary">
      <div className="container-custom grid grid-cols-1 md:grid-cols-2 gap-12 items-center">
        <motion.div
          initial={{ opacity: 0, x: -30 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <h2 className="text-3xl md:text-4xl font-display font-bold text-text-primary mb-4">
            Sală de Conferințe
          </h2>
          <p className="text-lg text-text-light mb-6">
            Spațiu profesional pentru evenimente corporate, cu toate echipamentele necesare.
          </p>
          <div className="mb-6">
            <div className="flex items-center space-x-3 mb-4">
              <UserGroupIcon className="w-6 h-6 text-primary-500" />
              <span className="text-text-secondary font-semibold">Capacitate: până la 40 de locuri</span>
            </div>
            <h3 className="text-lg font-semibold text-text-primary mb-3">Echipamente disponibile:</h3>
            <ul className="space-y-2">
              {equipment.map((item, idx) => {
                const IconComponent = item.icon;
                return (
                  <li key={idx} className="flex items-center space-x-3">
                    <span className="inline-flex items-center justify-center w-6 h-6 bg-primary-100 rounded-full">
                      <IconComponent className="w-4 h-4 text-primary-500" />
                    </span>
                    <span className="text-text-secondary text-sm">{item.text}</span>
                  </li>
                );
              })}
            </ul>
          </div>
          <div className="bg-primary-50 rounded-lg p-4">
            <h4 className="font-semibold text-primary-700 mb-2">Public țintă:</h4>
            <p className="text-primary-600 text-sm">Grupuri corporate, evenimente, workshopuri</p>
          </div>
        </motion.div>
        <motion.div
          initial={{ opacity: 0, x: 30 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
          className="relative"
        >
          <img
            src="https://images.unsplash.com/photo-1497366216548-37526070297c?ixlib=rb-4.0.3&auto=format&fit=crop&w=2070&q=80"
            alt="Sală de conferințe Hotel Marami"
            className="rounded-xl shadow-lg w-full h-80 object-cover"
            loading="lazy"
          />
        </motion.div>
      </div>
    </section>
  );
};

export default ConferenceRoomSection;
