import React from 'react';
import { motion } from 'framer-motion';
import { 
  InformationCircleIcon,
  ClockIcon,
  MapPinIcon,
  CloudIcon,
  BuildingOfficeIcon 
} from '@heroicons/react/24/outline';

const LogisticsTipsSection = () => {
  const tips = [
    {
      icon: "🅿️",
      title: "Parcare",
      description: "Verifică disponibilitatea – locuri limitate",
      iconComponent: MapPinIcon
    },
    {
      icon: "📅",
      title: "Rezervări",
      description: "Camerele cu vedere la munte sunt populare – rezervă din timp",
      iconComponent: ClockIcon
    },
    {
      icon: "🎫",
      title: "Bilete Turistice",
      description: "Rezervă online pentru atracții populare",
      iconComponent: BuildingOfficeIcon
    },
    {
      icon: "☁️",
      title: "Vremea",
      description: "Verifică prognoza înainte de drumeții",
      iconComponent: CloudIcon
    },
    {
      icon: "🍽️",
      title: "Restaurante",
      description: "Fă rezervări în avans în weekend-uri",
      iconComponent: InformationCircleIcon
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <section className="section-padding bg-background-primary">
      <div className="container-custom">
        {/* Section Header */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl md:text-4xl lg:text-5xl font-display font-bold text-text-primary mb-4">
            Sfaturi Logistice Utile
          </h2>
          <p className="text-lg text-text-light max-w-2xl mx-auto">
            Informații practice pentru a-ți planifica perfect șederea în Sinaia.
          </p>
        </motion.div>

        {/* Tips Grid */}
        <motion.div
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
        >
          {tips.map((tip, index) => {
            const IconComponent = tip.iconComponent;
            return (
              <motion.div
                key={index}
                variants={itemVariants}
                className="bg-white rounded-2xl p-6 shadow-lg hover:shadow-xl transition-shadow duration-300"
              >
                <div className="flex items-start space-x-4">
                  {/* Emoji Icon */}
                  <div className="text-3xl flex-shrink-0">
                    {tip.icon}
                  </div>

                  {/* Content */}
                  <div className="flex-1">
                    <h3 className="text-lg font-display font-bold text-text-primary mb-2">
                      {tip.title}
                    </h3>
                    <p className="text-text-light text-sm leading-relaxed">
                      {tip.description}
                    </p>
                  </div>

                  {/* Heroicon */}
                  <IconComponent className="w-5 h-5 text-primary-500 flex-shrink-0 mt-1" />
                </div>
              </motion.div>
            );
          })}
        </motion.div>
      </div>
    </section>
  );
};

export default LogisticsTipsSection;
