import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { 
  PhoneIcon,
  EnvelopeIcon,
  MapPinIcon,
  ClockIcon,
  ChatBubbleLeftIcon
} from '@heroicons/react/24/outline';

const ContactSection = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
    newsletter: false
  });

  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulate form submission
    await new Promise(resolve => setTimeout(resolve, 2000));

    setIsSubmitting(false);
    // Reset form
    setFormData({
      name: '',
      email: '',
      message: '',
      newsletter: false
    });

    alert('Mul»õumim pentru mesaj! Vom rƒÉspunde √Æn cel mai scurt timp.');
  };

  const contactInfo = [
    {
      icon: PhoneIcon,
      title: 'Telefon',
      value: '+40 244 123 456',
      subtitle: 'Recep»õie 24/7',
      link: 'tel:+40244123456'
    },
    {
      icon: EnvelopeIcon,
      title: 'Email',
      value: 'info@hotelmarami.ro',
      subtitle: 'RƒÉspuns √Æn 24h',
      link: 'mailto:info@hotelmarami.ro'
    },
    {
      icon: MapPinIcon,
      title: 'AdresƒÉ',
      value: 'Strada Pele»ôului, nr. 15',
      subtitle: 'Sinaia, Jude»õul Prahova',
      link: '#'
    },
    {
      icon: ClockIcon,
      title: 'Program',
      value: 'Check-in: 14:00',
      subtitle: 'Check-out: 11:00',
      link: '#'
    }
  ];

  const quickLinks = [
    { name: 'RezervƒÉri', href: '#', icon: 'üìÖ' },
    { name: 'WhatsApp', href: '#', icon: 'üí¨' },
    { name: 'FAQ', href: '#', icon: '‚ùì' },
    { name: 'Recenzii', href: '#', icon: '‚≠ê' }
  ];

  return (
    <section id="contact" className="section-padding bg-background-secondary">
      <div className="container-custom">
        {/* Section Header */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
