import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { CheckCircleIcon, PhoneIcon, EnvelopeIcon, MapPinIcon } from '@heroicons/react/24/outline';
import BlurText from '../BlurText';

const BookingConfirmationSection = () => {
  const handleAnimationComplete = () => {
    console.log('BookingConfirmation title animation completed!');
  };

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 2000));

    setIsSubmitting(false);
    setIsSubmitted(true);
  };

  const contactInfo = [
    {
      icon: PhoneIcon,
      title: 'Telefon',
      value: '+40 244 315 000',
      description: 'Disponibil 24/7'
    },
    {
      icon: EnvelopeIcon,
      title: 'Email',
      value: 'rezervari@hotelmarami.ro',
      description: 'Răspuns în 2 ore'
    },
    {
      icon: MapPinIcon,
      title: 'Adresa',
      value: 'Strada Furnica 52, Sinaia',
      description: 'România, 106100'
    }
  ];

  if (isSubmitted) {
    return (
      <section className="section-padding bg-gradient-to-br from-green-500 to-green-600">
        <div className="container-custom">
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8 }}
            className="text-center text-white"
          >
            <div className="w-24 h-24 bg-white/20 rounded-full flex items-center justify-center mx-auto mb-8">
              <CheckCircleIcon className="w-12 h-12 text-white" />
            </div>

            <BlurText
              text="Rezervare Trimisă!"
              className="text-3xl md:text-4xl lg:text-5xl font-display font-bold mb-6"
              delay={150}
              animateBy="words"
              direction="top"
              onAnimationComplete={handleAnimationComplete}
            />

            <p className="text-xl text-white/90 max-w-2xl mx-auto mb-8">
              Mulțumim pentru rezervare! Vom procesa cererea ta și vei primi o confirmare prin email în maxim 24 de ore.
            </p>

            <motion.div
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.4 }}
              className="bg-white/10 backdrop-blur-sm rounded-2xl p-8 max-w-2xl mx-auto"
            >
              <h3 className="text-xl font-display font-bold mb-4">
                Următorii Pași
              </h3>
              <div className="space-y-3 text-left">
                <div className="flex items-center space-x-3">
                  <div className="w-6 h-6 bg-white rounded-full flex items-center justify-center text-green-600 font-bold text-sm">1</div>
                  <span>Vei primi email de confirmare în 24 de ore</span>
                </div>
                <div className="flex items-center space-x-3">
                  <div className="w-6 h-6 bg-white rounded-full flex items-center justify-center text-green-600 font-bold text-sm">2</div>
                  <span>Verifică spam-ul dacă nu găsești email-ul</span>
                </div>
                <div className="flex items-center space-x-3">
                  <div className="w-6 h-6 bg-white rounded-full flex items-center justify-center text-green-600 font-bold text-sm">3</div>
                  <span>Contactează-ne dacă ai întrebări</span>
                </div>
              </div>
            </motion.div>
          </motion.div>
        </div>
      </section>
    );
  }

  return (
    <section className="section-padding bg-background-secondary">
      <div className="container-custom">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <BlurText
            text="Finalizează Rezervarea"
            className="text-3xl md:text-4xl lg:text-5xl font-display font-bold text-text-primary mb-4"
            delay={150}
            animateBy="words"
            direction="top"
            onAnimationComplete={handleAnimationComplete}
          />
          <p className="text-lg text-text-light max-w-2xl mx-auto">
            Apasă butonul de mai jos pentru a trimite rezervarea. Vom procesa cererea ta și vei primi o confirmare în curând.
          </p>
        </motion.div>

        <div className="max-w-4xl mx-auto">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* Submit Section */}
            <motion.div
              initial={{ opacity: 0, x: -30 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              <div className="bg-white rounded-2xl shadow-lg p-8">
                <h3 className="text-xl font-display font-bold text-text-primary mb-6">
                  Trimite Rezervarea
                </h3>

                <form onSubmit={handleSubmit} className="space-y-6">
                  <div className="bg-primary-50 rounded-xl p-6">
                    <h4 className="font-semibold text-text-primary mb-3">
                      Confirmare Finală
                    </h4>
                    <ul className="text-sm text-text-secondary space-y-2">
                      <li className="flex items-center space-x-2">
                        <CheckCircleIcon className="w-4 h-4 text-green-500 flex-shrink-0" />
                        <span>Toate câmpurile obligatorii sunt completate</span>
                      </li>
                      <li className="flex items-center space-x-2">
                        <CheckCircleIcon className="w-4 h-4 text-green-500 flex-shrink-0" />
                        <span>Termenii și condițiile sunt acceptați</span>
                      </li>
                      <li className="flex items-center space-x-2">
                        <CheckCircleIcon className="w-4 h-4 text-green-500 flex-shrink-0" />
                        <span>Datele personale sunt corecte</span>
                      </li>
                    </ul>
                  </div>

                  <motion.button
                    type="submit"
                    disabled={isSubmitting}
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    className={`w-full py-4 px-8 rounded-xl font-semibold text-lg transition-all duration-300 shadow-lg ${
                      isSubmitting
                        ? 'bg-gray-400 cursor-not-allowed'
                        : 'bg-primary-500 hover:bg-primary-600 hover:shadow-xl'
                    } text-white`}
                  >
                    {isSubmitting ? (
                      <div className="flex items-center justify-center space-x-2">
                        <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                        <span>Se trimite...</span>
                      </div>
                    ) : (
                      'Trimite Rezervarea'
                    )}
                  </motion.button>

                  <p className="text-xs text-text-secondary text-center">
                    Prin trimiterea acestei rezervări, confirm că am citit și accept termenii și condițiile.
                  </p>
                </form>
              </div>
            </motion.div>

            {/* Contact Information */}
            <motion.div
              initial={{ opacity: 0, x: 30 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: 0.4 }}
            >
              <div className="bg-white rounded-2xl shadow-lg p-8">
                <h3 className="text-xl font-display font-bold text-text-primary mb-6">
                  Ai Întrebări?
                </h3>

                <div className="space-y-6">
                  {contactInfo.map((contact, index) => {
                    const IconComponent = contact.icon;
                    return (
                      <motion.div
                        key={index}
                        initial={{ opacity: 0, y: 20 }}
                        whileInView={{ opacity: 1, y: 0 }}
                        viewport={{ once: true }}
                        transition={{ duration: 0.6, delay: index * 0.1 }}
                        className="flex items-start space-x-4"
                      >
                        <div className="w-12 h-12 bg-primary-100 rounded-full flex items-center justify-center flex-shrink-0">
                          <IconComponent className="w-6 h-6 text-primary-600" />
                        </div>
                        <div>
                          <h4 className="font-semibold text-text-primary mb-1">
                            {contact.title}
                          </h4>
                          <p className="text-text-primary font-medium">
                            {contact.value}
                          </p>
                          <p className="text-sm text-text-secondary">
                            {contact.description}
                          </p>
                        </div>
                      </motion.div>
                    );
                  })}
                </div>

                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.4 }}
                  className="mt-8 bg-primary-50 rounded-xl p-6"
                >
                  <h4 className="font-semibold text-text-primary mb-2">
                    Asistență 24/7
                  </h4>
                  <p className="text-sm text-text-secondary">
                    Echipa noastră este disponibilă oricând pentru a te ajuta cu rezervarea sau orice întrebări ai avea.
                  </p>
                </motion.div>
              </div>
            </motion.div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default BookingConfirmationSection;
