import React from 'react';
import { motion } from 'framer-motion';
import { LightBulbIcon } from '@heroicons/react/24/outline';

const RecommendationsSection = () => {
  const recommendations = [
    {
      icon: "üè∞",
      title: "Castelul Pele»ô",
      tip: "RezervƒÉ bilete online din timp"
    },
    {
      icon: "üõï",
      title: "M√¢nƒÉstirea Sinaia",
      tip: "ViziteazƒÉ diminea»õa pentru lini»ôte"
    },
    {
      icon: "üö°",
      title: "Telecabina Cota 1400",
      tip: "VerificƒÉ programul √Ænainte de urcare"
    },
    {
      icon: "ü•æ",
      title: "Trasee Bucegi",
      tip: "√énso»õe»ôte-te de ghid pe trasee dificile"
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, x: -20 },
    visible: { opacity: 1, x: 0 }
  };

  return (
    <section className="section-padding bg-white">
      <div className="container-custom">
        {/* Section Header */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl md:text-4xl lg:text-5xl font-display font-bold text-text-primary mb-4">
            RecomandƒÉri & Sfaturi Utile
          </h2>
          <p className="text-lg text-text-light max-w-2xl mx-auto">
            Sfaturi practice pentru a-»õi face experien»õa √Æn Sinaia c√¢t mai plƒÉcutƒÉ »ôi eficientƒÉ.
          </p>
        </motion.div>

        {/* Recommendations List */}
        <motion.div
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
          className="max-w-4xl mx-auto"
        >
          {recommendations.map((item, index) => (
            <motion.div
              key={index}
              variants={itemVariants}
              className="flex items-center space-x-4 p-6 bg-background-primary rounded-2xl mb-4 hover:shadow-lg transition-shadow duration-300"
            >
              {/* Icon */}
              <div className="text-3xl flex-shrink-0">
                {item.icon}
              </div>

              {/* Content */}
              <div className="flex-1">
                <h3 className="text-xl font-display font-bold text-text-primary mb-2">
                  {item.title}
                </h3>
                <div className="flex items-center space-x-2">
                  <LightBulbIcon className="w-5 h-5 text-primary-500 flex-shrink-0" />
                  <span className="text-text-light">{item.tip}</span>
                </div>
              </div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default RecommendationsSection;
