import React from 'react';
import { motion } from 'framer-motion';
import { 
  TvIcon,
  PhoneIcon,
  WifiIcon,
  HomeIcon,
  SparklesIcon,
  CheckIcon
} from '@heroicons/react/24/outline';

const ComfortableRoomsSection = () => {
  const amenities = [
    { icon: TvIcon, text: 'TV prin cablu' },
    { icon: PhoneIcon, text: 'Telefon' },
    { icon: WifiIcon, text: 'Acces Wi-Fi gratuit' },
    { icon: HomeIcon, text: 'Minibar' },
    { icon: SparklesIcon, text: 'Baie privată' },
    { icon: CheckIcon, text: 'Acces inclus la centrul de relaxare (saună, jacuzzi, fitness)' },
  ];

  return (
    <section className="section-padding bg-background-secondary">
      <div className="container-custom grid grid-cols-1 md:grid-cols-2 gap-12 items-center">
        <motion.div
          initial={{ opacity: 0, x: -30 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <h2 className="text-3xl md:text-4xl font-display font-bold text-text-primary mb-4">
            Camere Confortabile
          </h2>
          <p className="text-lg text-text-light mb-6">
            Camere decorate în nuanțe calde, dotate cu toate facilitățile moderne pentru confortul tău.
          </p>
          <ul className="space-y-3 mb-6">
            {amenities.map((amenity, idx) => {
              const IconComponent = amenity.icon;
              return (
                <li key={idx} className="flex items-center space-x-3">
                  <span className="inline-flex items-center justify-center w-8 h-8 bg-primary-100 rounded-full">
                    <IconComponent className="w-5 h-5 text-primary-500" />
                  </span>
                  <span className="text-text-secondary">{amenity.text}</span>
                </li>
              );
            })}
          </ul>
        </motion.div>
        <motion.div
          initial={{ opacity: 0, x: 30 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
          className="relative"
        >
          <img
            src="https://images.unsplash.com/photo-1631049307264-da0ec9d70304?ixlib=rb-4.0.3&auto=format&fit=crop&w=2070&q=80"
            alt="Camere confortabile Hotel Marami"
            className="rounded-xl shadow-lg w-full h-80 object-cover"
            loading="lazy"
          />
        </motion.div>
      </div>
    </section>
  );
};

export default ComfortableRoomsSection;
