import React from 'react';
import { motion } from 'framer-motion';
import { ArrowRightIcon, PhoneIcon } from '@heroicons/react/24/outline';

const ActivitiesCTASection = () => {
  return (
    <section className="section-padding bg-gradient-to-br from-primary-500 to-primary-600 text-white">
      <div className="container-custom">
        <div className="max-w-4xl mx-auto text-center">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            <h2 className="text-3xl md:text-4xl lg:text-5xl font-display font-bold mb-6">
              Vrei să organizezi o experiență personalizată în Sinaia?
            </h2>

            <p className="text-xl md:text-2xl font-light mb-8 leading-relaxed">
              Contactează-ne pentru recomandări, pachete sau ghizi locali.
            </p>

            <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className="inline-flex items-center space-x-2 bg-white text-primary-500 px-8 py-4 rounded-xl font-medium hover:bg-gray-100 transition-colors duration-300 text-lg"
              >
                <span>Organizează Activitatea</span>
                <ArrowRightIcon className="w-6 h-6" />
              </motion.button>

              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className="inline-flex items-center space-x-2 border-2 border-white text-white px-8 py-4 rounded-xl font-medium hover:bg-white hover:text-primary-500 transition-colors duration-300 text-lg"
              >
                <PhoneIcon className="w-6 h-6" />
                <span>Contactează-ne</span>
              </motion.button>
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default ActivitiesCTASection;
