import React from 'react';
import { motion } from 'framer-motion';
import { 
  TruckIcon,
  ShieldCheckIcon,
  PhoneIcon,
  SparklesIcon,
  MapIcon,
  UserGroupIcon
} from '@heroicons/react/24/outline';

const AdditionalServicesSection = () => {
  const services = [
    { 
      icon: TruckIcon, 
      title: 'Parcare privată', 
      description: 'Contra cost, locuri limitate' 
    },
    { 
      icon: PhoneIcon, 
      title: 'Room-service', 
      description: 'Servicii la cameră disponibile' 
    },
    { 
      icon: ShieldCheckIcon, 
      title: 'Cutie valori', 
      description: 'Seif pentru valorile tale' 
    },
    { 
      icon: TruckIcon, 
      title: 'Transfer privat', 
      description: 'Aeroport / gară (la cerere)' 
    },
    { 
      icon: SparklesIcon, 
      title: 'Cursuri de schi', 
      description: 'Cu instructor autorizat' 
    },
    { 
      icon: MapIcon, 
      title: 'Vizite ghidate', 
      description: 'La atracții istorice sau naturale' 
    },
  ];

  return (
    <section className="section-padding bg-background-primary">
      <div className="container-custom">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl md:text-4xl font-display font-bold text-text-primary mb-4">
            Servicii Suplimentare
          </h2>
          <p className="text-lg text-text-light max-w-2xl mx-auto">
            Servicii personalizate pentru a-ți face șederea la Hotel Marami și mai specială.
          </p>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8, delay: 0.2 }}
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
        >
          {services.map((service, index) => {
            const IconComponent = service.icon;
            return (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                className="bg-white rounded-xl p-6 shadow-lg card-hover"
              >
                <div className="w-12 h-12 bg-primary-100 rounded-lg flex items-center justify-center mb-4">
                  <IconComponent className="w-6 h-6 text-primary-500" />
                </div>
                <h3 className="font-display font-semibold text-text-primary mb-2">
                  {service.title}
                </h3>
                <p className="text-text-light text-sm">
                  {service.description}
                </p>
              </motion.div>
            );
          })}
        </motion.div>

        {/* Call to Action */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8, delay: 0.4 }}
          className="text-center mt-16"
        >
          <div className="bg-gradient-to-r from-primary-50 to-background-secondary rounded-2xl p-8">
            <h3 className="text-2xl font-display font-bold text-text-primary mb-4">
              Ai nevoie de servicii speciale?
            </h3>
            <p className="text-text-light mb-6">
              Contactează-ne pentru a discuta despre serviciile personalizate și pentru a-ți organiza șederea perfectă.
            </p>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className="btn-primary"
            >
              Contactează-ne
            </motion.button>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default AdditionalServicesSection;
