import React from 'react';
import { motion } from 'framer-motion';

const SeasonalInfoSection = () => {
  const seasons = [
    {
      icon: "🌸",
      name: "Primăvară",
      highlights: "Natură înfloritoare, prețuri ok",
      activities: "Trasee ușoare, Peleș, fotografie",
      color: "bg-pink-50 border-pink-200"
    },
    {
      icon: "☀️",
      name: "Vară",
      highlights: "Toate activitățile, vreme excelentă",
      activities: "Telecabină, excursii montane",
      color: "bg-yellow-50 border-yellow-200"
    },
    {
      icon: "🍁",
      name: "Toamnă",
      highlights: "Culori, liniște, oferte bune",
      activities: "Trasee montane, fotografie, tururi",
      color: "bg-orange-50 border-orange-200"
    },
    {
      icon: "❄️",
      name: "Iarnă",
      highlights: "Zăpadă, sărbători, schi",
      activities: "Schi Cota 1400, Castelul iarna",
      color: "bg-blue-50 border-blue-200"
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const cardVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <section className="section-padding bg-white">
      <div className="container-custom">
        {/* Section Header */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl md:text-4xl lg:text-5xl font-display font-bold text-text-primary mb-4">
            Informații Sezonale
          </h2>
          <p className="text-lg text-text-light max-w-2xl mx-auto">
            Descoperă ce face fiecare anotimp special în Sinaia și ce activități îți recomandăm.
          </p>
        </motion.div>

        {/* Seasons Grid */}
        <motion.div
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6"
        >
          {seasons.map((season, index) => (
            <motion.div
              key={index}
              variants={cardVariants}
              className={`${season.color} border-2 rounded-2xl p-6 hover:shadow-lg transition-shadow duration-300`}
            >
              {/* Season Icon & Name */}
              <div className="text-center mb-4">
                <div className="text-4xl mb-2">{season.icon}</div>
                <h3 className="text-xl font-display font-bold text-text-primary">
                  {season.name}
                </h3>
              </div>

              {/* Highlights */}
              <div className="mb-4">
                <h4 className="text-sm font-semibold text-text-primary mb-2 uppercase tracking-wide">
                  Highlights
                </h4>
                <p className="text-sm text-text-light">
                  {season.highlights}
                </p>
              </div>

              {/* Recommended Activities */}
              <div>
                <h4 className="text-sm font-semibold text-text-primary mb-2 uppercase tracking-wide">
                  Activități Recomandate
                </h4>
                <p className="text-sm text-text-light">
                  {season.activities}
                </p>
              </div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default SeasonalInfoSection;
