import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { 
  PhoneIcon,
  EnvelopeIcon,
  MapPinIcon,
  ClockIcon
} from '@heroicons/react/24/outline';
import BlurText from './BlurText';

const ContactSection = () => {
  const handleAnimationComplete = () => {
    console.log('Contact title animation completed!');
  };

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
    newsletter: false
  });

  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulate form submission
    await new Promise(resolve => setTimeout(resolve, 2000));

    setIsSubmitting(false);
    // Reset form
    setFormData({
      name: '',
      email: '',
      message: '',
      newsletter: false
    });

    alert('MulÈ›umim pentru mesaj! Vom rÄƒspunde Ã®n cel mai scurt timp.');
  };

  const contactInfo = [
    {
      icon: PhoneIcon,
      title: 'Telefon',
      value: '+40 759 033 047',
      subtitle: 'RecepÈ›ie 24/7',
      link: 'tel:+40759033047'
    },
    {
      icon: EnvelopeIcon,
      title: 'Email',
      value: 'info@hotelmarami.ro',
      subtitle: 'RÄƒspuns Ã®n 24h',
      link: 'mailto:info@hotelmarami.ro'
    },
    {
      icon: MapPinIcon,
      title: 'AdresÄƒ',
