import React from 'react';
import { motion } from 'framer-motion';
import { StickyScroll } from '../../ui/sticky-scroll-reveal';
import BlurText from '../BlurText';

const FacilitiesStickyScrollSection = () => {
  const handleAnimationComplete = () => {
    console.log('FacilitiesStickyScroll title animation completed!');
  };

  const facilitiesContent = [
    {
      title: "Camere Confortabile",
      description: "Camerele noastre sunt proiectate pentru a oferi confortul și relaxarea pe care le meriți. Fiecare cameră este curățată zilnic și întreținută conform celor mai înalte standarde. Design modern, dotări complete și siguranță garantată pentru o ședere perfectă.",
      content: (
        <div className="flex items-center justify-center h-full">
          <img
            src="https://images.unsplash.com/photo-1631049307264-da0ec9d70304?auto=format&fit=crop&w=900&q=80"
            alt="Camere confortabile"
            className="w-full h-full object-cover"
          />
        </div>
      )
    },
    {
      title: "Restaurant & Bar",
      description: "Restaurantul nostru cu 40 de locuri oferă o experiență culinară excepțională cu bucătărie românească și mediteraneană. Barul modern servește cocktailuri speciale și băuturi de calitate. Room service disponibil și evenimente private și corporate.",
      content: (
        <div className="flex items-center justify-center h-full">
          <img
            src="https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?auto=format&fit=crop&w=900&q=80"
            alt="Restaurant și bar"
            className="w-full h-full object-cover"
          />
        </div>
      )
    },
    {
      title: "Centru de Relaxare",
      description: "Centrul nostru de relaxare include saună, jacuzzi și facilități de spa pentru a te ajuta să te relaxezi complet. Acces gratuit pentru toți oaspeții hotelului. O experiență de wellness completă în inima munților Bucegi.",
      content: (
        <div className="flex items-center justify-center h-full">
          <img
            src="https://images.unsplash.com/photo-1540555700478-4be289fbecef?auto=format&fit=crop&w=900&q=80"
            alt="Centru de relaxare"
            className="w-full h-full object-cover"
          />
        </div>
      )
    },
    {
      title: "Sala de Conferințe",
      description: "Sala de conferințe modernă, perfectă pentru evenimente corporate, seminarii și întâlniri de afaceri. Dotată cu tehnologie de ultimă generație și capacitate pentru grupuri mari. Servicii complete de organizare evenimente.",
      content: (
        <div className="flex items-center justify-center h-full">
          <img
            src="https://images.unsplash.com/photo-1497366216548-37526070297c?auto=format&fit=crop&w=900&q=80"
            alt="Sala de conferințe"
            className="w-full h-full object-cover"
          />
        </div>
      )
    },
    {
      title: "Sala Fitness",
      description: "Sala de fitness modernă cu echipamente de ultimă generație pentru a-ți menține rutina de antrenament în timpul șederii. Acces gratuit pentru toți oaspeții. Program flexibil pentru a se potrivi cu programul tău.",
      content: (
        <div className="flex items-center justify-center h-full">
          <img
            src="https://images.unsplash.com/photo-1571019613454-1cb2f99b2d8b?auto=format&fit=crop&w=900&q=80"
            alt="Sala fitness"
            className="w-full h-full object-cover"
          />
        </div>
      )
    }
  ];

  return (
    <section className="section-padding bg-background-primary">
      <div className="container-custom">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <BlurText
            text="Facilitățile Hotelului Marami"
            className="text-3xl md:text-4xl lg:text-5xl font-display font-bold text-text-primary mb-4"
            delay={150}
            animateBy="words"
            direction="top"
            onAnimationComplete={handleAnimationComplete}
          />
          <p className="text-lg text-text-light max-w-3xl mx-auto">
            Descoperă toate facilitățile pe care le oferim pentru a-ți face șederea la Hotel Marami 
            o experiență memorabilă și relaxantă.
          </p>
        </motion.div>

        <StickyScroll content={facilitiesContent} />
      </div>
    </section>
  );
};

export default FacilitiesStickyScrollSection;
