import React from 'react';
import { motion } from 'framer-motion';

const SocialLinksSection = () => {
  const socialLinks = [
    {
      name: 'Facebook',
      url: 'https://facebook.com/hotelmarami',
      icon: '📘',
      color: 'bg-blue-600 hover:bg-blue-700',
      description: 'Urmărește-ne pentru noutăți și oferte speciale'
    },
    {
      name: 'Instagram',
      url: 'https://instagram.com/hotelmarami',
      icon: '📷',
      color: 'bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600',
      description: 'Descoperă atmosfera hotelului prin pozele noastre'
    },
    {
      name: 'TripAdvisor',
      url: 'https://tripadvisor.com/hotelmarami',
      icon: '⭐',
      color: 'bg-green-600 hover:bg-green-700',
      description: 'Vezi recenziile și experiențele oaspeților noștri'
    },
    {
      name: 'Booking.com',
      url: 'https://booking.com/hotelmarami',
      icon: '🏨',
      color: 'bg-blue-500 hover:bg-blue-600',
      description: 'Rezervă direct cu prețuri garantate'
    }
  ];

  return (
    <section className="section-padding bg-background-secondary">
      <div className="container-custom">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl md:text-4xl font-display font-bold text-text-primary mb-4">
            Rețele Sociale & Linkuri Utile
          </h2>
          <p className="text-lg text-text-light max-w-2xl mx-auto">
            Conectează-te cu noi și descoperă mai multe despre experiența Hotel Marami.
          </p>
        </motion.div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {socialLinks.map((link, index) => (
            <motion.a
              key={index}
              href={link.url}
              target="_blank"
              rel="noopener noreferrer"
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: index * 0.1 }}
              whileHover={{ y: -5, scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              className={`${link.color} text-white rounded-2xl p-6 shadow-lg hover:shadow-xl transition-all duration-300 text-center group`}
            >
              <div className="text-4xl mb-4 group-hover:scale-110 transition-transform duration-300">
                {link.icon}
              </div>
              <h3 className="text-xl font-bold mb-2">
                {link.name}
              </h3>
              <p className="text-sm opacity-90">
                {link.description}
              </p>
            </motion.a>
          ))}
        </div>

        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8, delay: 0.4 }}
          className="mt-12 bg-white rounded-2xl shadow-lg p-8 text-center"
        >
          <h3 className="text-2xl font-bold text-text-primary mb-4">
            Rămâi Conectat
          </h3>
          <p className="text-text-secondary mb-6">
            Abonează-te la newsletter-ul nostru pentru oferte exclusive și noutăți.
          </p>
          <div className="flex flex-col sm:flex-row gap-4 max-w-md mx-auto">
            <input
              type="email"
              placeholder="Adresa ta de email"
              className="flex-1 px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            />
            <button className="bg-primary-500 hover:bg-primary-600 text-white px-6 py-3 rounded-lg font-semibold transition-colors">
              Abonează-te
            </button>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default SocialLinksSection;
